    typealias SwiftType = Status

    public static func read(from buf: inout (data: Data, offset: Data.Index)) throws -> Status {
        let variant: Int32 = try readInt(&buf)
        switch variant {
        
        case 1: return .waitingForConnection
        
        case 2: return .awaitingConfirmation
        
        case 3: return .confirmed(proof: try FfiConverterTypeProof.read(from: &buf)
        )
        
        case 4: return .failed(error: try FfiConverterString.read(from: &buf)
        )
        
        default: throw UniffiInternalError.unexpectedEnumCase
        }
    }

    public static func write(_ value: Status, into buf: inout [UInt8]) {
        switch value {
        
        
        case .waitingForConnection:
            writeInt(&buf, Int32(1))
        
        
        case .awaitingConfirmation:
            writeInt(&buf, Int32(2))
        
        
        case let .confirmed(proof):
            writeInt(&buf, Int32(3))
            FfiConverterTypeProof.write(proof, into: &buf)
            
        
        case let .failed(error):
            writeInt(&buf, Int32(4))
            FfiConverterString.write(error, into: &buf)
            
        }
    }
}


